import CIcon from "@coreui/icons-react";
import React, { useState, useEffect } from "react";
import axios from "axios";
import Joi from "joi-browser";
import Swal from "sweetalert2";
//import NavHeader from "../../components/navHeader";
//import { toast } from "react-toastify";

import {
  //CBadge,
  CCard,
  CCardBody,
  //CCardHeader,
  CCol,
  //CDataTable,
  CRow,
  CButton,
  CModal,
  CModalFooter,
  CModalHeader,
  CModalBody,
  CCardHeader,
  CInputGroupAppend,
  CInputGroupPrepend,
  //CDropdownItem,
  CForm,
  //CCardFooter,
  //CDropdownToggle,
  //CInputRadio,
  //CDropdown,
  CModalTitle,
  //CFormText,
  //CTextarea,
  CFormGroup,
  CLabel,
  // CSwitch,
  CInput,
  //CInputFile,
  //CSelect,
  //CDropdownMenu,
  //CCardFooter,
  CInputGroup,
  //CForm,
} from "@coreui/react";
//import { DocsLink } from 'src/reusable'

//import usersData from './psm/users/usersData';

//import usersData from '../users/UsersData';

const GradeSetup = () => {
  const [gradeData, setGradeData] = useState([]);

  const getBadge = (status) => {
    switch (status) {
      case "Active":
        return "success";
      case "Inactive":
        return "secondary";
      case "Pending":
        return "warning";
      case "Banned":
        return "danger";
      default:
        return "primary";
    }
  };

  //{id: 0, name: 'John Doe', registered: '2018/01/01', role: 'Guest', status: 'Pending', ok: 'Pending'}
  const fields = ["_id", "Grade", "Action"];
  const [visible, setVisible] = useState(false);
  const [search, setSearch] = useState("");

  const [grades, setGrades] = useState([]);
  const [disableButton, setDisableButton] = useState(false);

  useEffect(() => {
    async function getGrades() {
      const results = await axios.get(
        "https://ugmcservice.herokuapp.com/api/grades"
      );
      setGrades(results.data);
    }
    getGrades();
  }, [grades]);

  const [grade, setGrade] = useState("");
  const [level, setLevel] = useState("");
  const [days, setDays] = useState("");

  const joiGradeSchema = Joi.object().keys({
    grade: Joi.string().required().label("Grade"),
    level: Joi.string().required().label("Level"),
    days: Joi.number().label("Days"),
  });

