import CIcon from "@coreui/icons-react";
import React, { useState, useEffect } from "react";
import axios from "axios";
import Joi from "joi-browser";
import Swal from "sweetalert2";
import {
  CBadge,
  CCard,
  CCardBody,
  //CCardHeader,
  CCol,
  CDataTable,
  CRow,
  CButton,
  CModal,
  CModalFooter,
  CModalHeader,
  CModalBody,
  CCardHeader,
  CInputGroupAppend,
  CInputGroupPrepend,
  CDropdownItem,
  CForm,
  //CCardFooter,
  CDropdownToggle,
  //CInputRadio,
  CDropdown,
  CModalTitle,
  //CFormText,
  //CTextarea,
  CFormGroup,
  CLabel,
  // CSwitch,
  CInput,
  //CInputFile,
  //CSelect,
  CDropdownMenu,
  //CCardFooter,
  CInputGroup,
  //CForm,
} from "@coreui/react";
//import { DocsLink } from 'src/reusable'

//import usersData from './psm/users/usersData';

import usersData from "../users/UsersData";
import DepartmentSetup from "./Directorate";

const UnitSetup = () => {
  const [unit, setUnit] = useState("");
  const [units, setUnits] = useState([]);
  const [department, setDepartment] = useState([]);
  const [selectedDepartment, setSelectedDepartment] = useState("");
  const [disableButton, setDisableButton] = useState(false);
  const [search, setSearch] = useState("");
  const [iUnit, setIUnit] = useState({
    unit: "",
    department: "",
  });
  const joiUnitSchema = Joi.object().keys({
    unit: Joi.string().required().label("Unit"),
    department: Joi.string().required().label("Department"),
  });

  const validateEntry = () => {
    const result = Joi.validate({ unit, department }, joiUnitSchema);
    if (result.error) {
      return result.error.details[0].message;
    } else {
      return null;
    }
  };

  useEffect(() => {
    async function getUnits() {
      const results = await axios.get(
        "https://ugmcservice.herokuapp.com/api/units"
      );
      setUnits(results.data);
    }
    getUnits();
  }, [units]);

  useEffect(() => {
    async function getDepartmentsInHooks() {
      const results = await axios.get(
        "https://ugmcservice.herokuapp.com/api/departments"
      );
      setDepartment(results.data);
    }
    getDepartmentsInHooks();
  }, []);

  const handleDepartmentChange = (e) => {
    //setSelectedDepartment(e.target.value);
    setIUnit({ ...iUnit, department: e.currentTarget.value });
    console.log(e.currentTarget.value);
  };

  const handleUnitChange = (e) => {
    setIUnit({ ...iUnit, unit: e.currentTarget.value });
  };

  const handleSubmit = async () => {
    console.log(iUnit);
    setDisableButton(true);
    const results = await axios.post(
      "https://ugmcservice.herokuapp.com/api/units",
      iUnit
    );

    if (results.status === 200) {
      Swal.fire({
        title: "Success",
        text: "Unit Successfully Added",
        icon: "success",
      });
      setDisableButton(false);
    } else {
      Swal.fire({
        title: "Success",
        text: "Unit Setup Failed",
        icon: "error",
      });
      setDisableButton(false);
    }
    setDisableButton(false);
    setUnit("");
    setSelectedDepartment("");
    setIUnit({ unit: "", department: "" });
  };

  const getBadge = (status) => {
    switch (status) {
      case "Active":
        return "success";
      case "Inactive":
        return "secondary";
      case "Pending":
        return "warning";
      case "Banned":
        return "danger";
      default:
        return "primary";
    }
  };
  const fields = [
    "name",
    "Phone Number",
    "GH-ID",
    "registered",
    "role",
    "status",
  ];
  const [visible, setVisible] = useState(false);

  const handleSearch = (e) => {
