			if (results.status !== 200) {
				return Swal.fire(
					'OOPs !',
					'Submission Failed ! Check Entries and try again !',
					'error'
				);
			} else {
				Swal.fire('Good job!', 'Cheque Disbursed Successfully', 'success');
				setRender(!render);
				setDisburseData({
					chequeId: 0,
					disbursedBy: 0,
					amount: '',
